mapper.insert:

	select reply_seq.nextval
	from dual

INSERT INTO reply(rep_num, qna_num, rep_name, rep_content

	,rep_file

)
VALUES(
#{rep_num},#{qna_num},#{rep_name},#{rep_content}

	,#{rep_file, jdbcType=VARCHAR}

)

mapper.select:
SELECT m_id, m_num, m_name, m_pwd, m_gender, m_phone, m_email, m_memo, m_state, to_char(m_date,'YYYY-MM-DD') as m_date
FROM member
WHERE m_id = #{m_id}
