mapper.update:
UPDATE designer
SET des_job= #{des_job}, des_phone=#{des_phone}, des_gender=#{des_gender}, des_holyday=#{des_holyday}, des_file=#{des_file,jdbcType=VARCHAR}, des_memo=#{des_memo},des_image = #{des_image,jdbcType=VARCHAR}
WHERE des_num = #{des_num}

UPDATE designer
SET des_state = 0
WHERE des_num = #{des_num}

UPDATE designer
SET des_state = 1
WHERE des_num = #{des_num}
