FROM( 
	SELECT list.*, ROWNUM as RNUM
	FROM (

	) list
	)



			RNUM BETWEEN #{start_row} AND #{end_row}

mapper.insert:
SELECT hairgoods_seq.nextval FROM dual
INSERT INTO hairgoods( hg_num, hg_title, hg_content, hg_thumb
,hg_img1
) VALUES( #{hg_num}, #{hg_title}, #{hg_content}, #{hg_thumb}
,#{hg_img1}
)

mapper.update:
update hairgoods
set hg_title=#{hg_title},
hg_content=#{hg_content}

, hg_thumb = #{hg_thumb}


, hg_img1 = #{hg_img1}

where hg_num = #{hg_num}
