mapper.insert:
SELECT hairstyle_seq.nextval FROM dual
INSERT INTO hairstyle( hs_num, hs_title, hs_content, hs_thumb
,hs_img1
) VALUES( #{hs_num}, #{hs_title}, #{hs_content}, #{hs_thumb}
,#{hs_img1}
)

mapper.update:
update hairstyle
set hs_title=#{hs_title},
hs_content=#{hs_content}

,hs_thumb=#{hs_thumb}


,hs_img1=#{hs_img1}

where hs_num = #{hs_num}
