mapper.select:
SELECT COUNT(*)
FROM MEMBER
WHERE m_id = #{m_id}

SELECT COUNT(*)
FROM MEMBER
WHERE m_email = #{m_email}

mapper.update:
UPDATE member 
SET m_phone = #{m_phone}, m_memo = #{m_memo}
WHERE m_num = #{m_num}

UPDATE member
SET m_state = 0
WHERE m_id = #{m_id}

UPDATE reservation
SET rest_state = 0
WHERE rest_num = #{rest_num}
