mapper.select:
SELECT COUNT(*)
FROM MEMBER
WHERE m_id = #{m_id}

SELECT *
FROM MEMBER
WHERE m_id = #{m_id}
AND m_pwd = #{m_pwd}

SELECT m_id, m_num, m_name, m_pwd, m_gender, m_phone, m_email, m_memo, m_state, to_char(m_date,'YYYY-MM-DD') as m_date
FROM MEMBER
WHERE m_id = #{m_id}

SELECT COUNT(*)
FROM MEMBER
WHERE m_email = #{m_email}

mapper.update:
UPDATE member
SET m_state = 0
WHERE m_num = #{m_num}
